import './card.css'
import React, {useState} from 'react'
import CardBody from './cardBody/CardBody';

const Card = (props) => {
  const [isClicked, setIsClicked] = useState(false);

  const handleClickCard = () => {
    setIsClicked((prev) => !prev)
  }

  return (
  <div className={`${isClicked ? 'clicked' : 'unclicked'}`} onClick={handleClickCard} >
    { isClicked ? 
    <CardBody isClicked={isClicked} side={'back'} content={props.content}/> 
    :
    <CardBody side={'front'} isClicked={isClicked} content={props.title}>
      <img className={`card_img ${isClicked ? 'clicked' : 'unclicked'}`} src={`${props.content.toLowerCase()}.jpg`} alt={props.title} />
    </CardBody>
    }
  </div>
  )
}

export default Card
